import React from 'react';
import '../index.css';
import {NavLink, Route, Routes} from 'react-router-dom';
import './Nav.css';
import 'bootstrap/dist/css/bootstrap.css';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import Home from '../Home/home';
import About from '../Home/About';
import Contact from '../Home/Contact';
import Service from '../Home/Service';
import SingleMovie from '../Home/SingleMovie';
import Error from '../Home/Error';



function Nav() {
    return (
      <>
       
        <div className='main bg-dark py-1 px-3 d-flex align-items-center justify-content-between position-sticky'>
        
            <div className='logo px-3'>
                <img src="images/logomovie.png" alt="" />
            </div>
            <div className='menu px-3 d-flex justify-content-around'>
                <div className=' menu-item px-4 py-2 mx-3 '><NavLink  activeclassName="active_class" className="menu-text text-light" to="/home"  > Home</NavLink> </div>
                <div className=' menu-item px-4 py-2 mx-3 '><NavLink    className="menu-text text-light" to="/about" > About</NavLink> </div>
                <div className=' menu-item px-4 py-2 mx-3 '><NavLink    className="menu-text text-light" to="/contact" >Contact</NavLink> </div>
                <div className=' menu-item px-4 py-2 mx-3 '><NavLink    className="menu-text text-light" to="/service" >Services</NavLink> </div>
            </div>
           
            <div className='d-flex justify-content-between align-items-center'>
                <input type="text" placeholder='Search' className='bg-dark px-3 py-2 rounded fs-16' />
                <AccountCircleIcon style={{color:"white",fontSize:"45px",}} className='mx-3'></AccountCircleIcon>
            </div>
        </div>

        <Routes>
            <Route exact path='/home'  element={<Home/>}/>
            <Route eaxct path='/home/movies/:id' element={<SingleMovie/>}/>
            <Route eaxct path='/contact'  element={<Contact/>}/>
            <Route eaxct path='/about'  element={<About/>}/>
            <Route eaxct path='/service'  element={<Service/>}/>
            <Route exact path="*" element={<Error/>}/>
        </Routes>

      </>
    );
}

export default Nav;
