import React, { createContext } from 'react';


const AppContext=React.createContext();
const AppProvider=({children})=>{
   
   
    return (
        <>
       <AppContext.Provider value="SUDHA">
        {children}
       </AppContext.Provider>     
        </>
    );

}
export {AppProvider,AppContext};
