import React, { createContext,useContext, useEffect, useState } from 'react';
import Movie from './Home/Movie';


const AppContext=React.createContext();

const AppProvider=({children})=>{

    const[isLoading,setIsLoading]=useState(true);
    const[movie,setMovie]=useState([]);
    const[isError,setIsError]=useContext({show:"false",msg:""});

    const url="https://omdbapi.com/?apikey=9f698cae&s=titanic";
    const getApi=async()=>{
     try {
         let getData=await fetch(url);
         getData=getData.json();
         console.log(getData);
         if(getData.Response==="true")
         {
            setIsLoading(false);
            setMovie(getData.Search);
            
         }else{
            setIsError({
                show:"true",
                msg:getData.error
             })
         }
       
   
     } catch (error) {
        console.log(error);
     }
    }

    useEffect(()=>{
        getApi();
    },[]);
    
   return (<AppContext.Provider value={movie}>
 

  <Movie/>
   </AppContext.Provider>
   );  
       
}

    //GLOBAL context hook

    const useGlobalContext=()=>{
        return useContext(AppContext);
    }


export {AppProvider,AppContext,useGlobalContext};
