import React,{useEffect,useState} from 'react'
import { useParams } from 'react-router-dom';
import { url,AppContext,AppProvider } from '../Context';


 const SingleMovie = () => {
  const {id}=useParams();
  const[isLoading,setIsLoading]=useState(true);
  const[movie,setMovie]=useState("");
 

   

    const getApi=async(url)=>{
        setIsLoading(true);
     try {
       const res=await fetch(url);
       const data= await res.json();
       
       if(data.Response==="True")
         {
            setIsLoading(false);
           
            setMovie(data);
            console.log(data);
            
         }
   
     } catch (error) {
        console.log(error);
     }
    }

    useEffect(()=>{

       const timeout=setTimeout(() => {
            getApi(`${url}&i=${id}`);   
        }, 800);

        return ()=>clearTimeout(timeout);
        
    },[id]);


    if(isLoading){
      return <div className='loading'>
        <p>Loading ...</p>
      </div>
    }
    

    return (
      <>
      <div className='movie-section'>
        <div className='movie-card'>
          <figure>
            <img src={movie.Poster} alt="" />
          </figure>
          <div className='card-content'>
          <p className='title'>Title:{movie.Title}</p> 
          <p className='realesed'>Released:{movie.Released}</p>
          <p className='actors'>Actors:{movie.Actors}</p>
          <p className='title'>Country:{movie.Country}</p>
          <p className='title'>Director:{movie.Director}</p>
          </div>
        </div>
      </div>
      </>
    )
}

export default SingleMovie;
