import React,{useContext} from 'react';
import SimpleImageSlider from "react-simple-image-slider";
import './Home.css';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import { useGlobalContext } from '../Context';



export const home = () => {
    
    const data = useGlobalContext;
    const sliderImages=[
        {
            url: "https://cdn.zeebiz.com/sites/default/files/2023/05/22/243470-fast-x-twitter.jpg",
         },
         {
            url: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQR0F7BZ-tVbS3v3aZV1PvdKo3LGD-Nmzj50ujAa4IJl-zlbjrzsV65YOF80KMKhc3-wXo&usqp=CAU",
         },
         {
            url: "https://encryptedtbn0.gstatic.com/images?q=tbn:ANd9GcSprPgYofGmXXPfuEDcZ_XI294n0bME5dTX9TGvINmPiA&s",
         },
         {
            url: "https://i.pinimg.com/474x/81/ca/47/81ca47eaae35615ba9a9bb57560aaa3c.jpg",
         },
         {
            url: "https://encryptedtbn0.gstatic.com/images?q=tbn:ANd9GcTof2fniv0mZzN8DByLmb6ILU4MvV_SGr_wptMeAut_dPaYMBkeHnHhD5egzU7MB0GSqE&usqp=CAU",
         }
    ]

    return(
        <>
       <SimpleImageSlider
      
            width={1260}
            height={400}
            images={sliderImages}
            showNavs={true}
       />
       
       <div className="highlights my-5 mx-5">
       <h2 className="text-dark">Hightlights Movie</h2>
       <div className="highlights d-flex justify-content-between mt-5 align-items-center">
       <ArrowBackIcon></ArrowBackIcon>
          <div className=" item mx-3 mx-3">
            <img  src="https://m.timesofindia.com/thumb.cms?msid=88171876&width=200&height=281" alt="" />
          </div>

          <div className=" item mx-3 mx-3">
            <img src="https://m.timesofindia.com/thumb.cms?msid=88171876&width=200&height=281" alt="" />
          </div>

          <div className=" item mx-3 mx-3">
            <img src="https://m.timesofindia.com/thumb.cms?msid=88171876&width=200&height=281" alt="" />
          </div>

          <div className=" item mx-3 mx-3">
            <img src="https://m.timesofindia.com/thumb.cms?msid=88171876&width=200&height=281" alt="" />
          </div>

          <div className=" item mx-3">
            <img src="https://m.timesofindia.com/thumb.cms?msid=88171876&width=200&height=281" alt="" />
          </div>
          <div className=" item mx-3">
            <img src="https://m.timesofindia.com/thumb.cms?msid=88171876&width=200&height=281" alt="" />
          </div>

          <div className=" item mx-3">
            <img src="https://m.timesofindia.com/thumb.cms?msid=88171876&width=200&height=281" alt="" />
          </div>
          <ArrowForwardIcon></ArrowForwardIcon>

       </div>

       </div>
      <h1>{data}</h1>
     </>
    );
}

export default home;
