import React, { useContext } from 'react';
import { AppContext ,AppProvider} from '../Context';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';


export const Movie = () => {
    const {movie}=useContext(AppContext);
   
    return (
        <>

<div className="highlights my-5 mx-5">
       <h2 className="text-dark">Hightlights Movie</h2>
 </div>      
       {
        movie.map((current,index)=>{

          return (
            <>
           
       <div className="highlights d-flex justify-content-between mt-5 align-items-center">
       
          <div className=" item mx-3 mx-3">
            <img  src="https://m.timesofindia.com/thumb.cms?msid=88171876&width=200&height=281" alt="" />
          </div>

          
       

       </div>

      
            <div>
                <h1>{current.Title}</h1>
            </div>    
            </>
           
          );

        })
       }
        </>
    );
}

export default Movie;
