import React, { useContext } from 'react';
import { AppContext ,AppProvider} from '../Context';
import {NavLink} from 'react-router-dom';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import 'animate.css';


 const Movie = () => {
    const {movie}=useContext(AppContext);
     return (
     <>

        <div className="highlights my-5 mx-5">
            <h2 className="text-dark">Hightlights Movie</h2>
        </div>   
        {/* <div className=" container d-flex justify-content-between flex-wrap mx-5">  
        {
            movie.map((current,index)=>{
                const{imdbID,Title,Poster}=current;
            return <NavLink to={`/movies/${imdbID}`}  >
               <div className="item  my-3">
                   <div className='item-description'>
                   <h5 className='text-center'>{Title}</h5>
                   <img  src={Poster} alt="" />
                   </div>
                   </div>
            </NavLink>
           })}
         </div>  */}

         <div className='container-fluid'>
            <div className='row'>
             <div className='col-11 mx-auto'>
               <div className="row my-5">
               <div className='item1 col-12 col-lg-4 col-md-4 '>
               {
                movie.map((current,index)=>{
                const{imdbID,Title,Poster}=current;
              return <NavLink to={`/movies/${imdbID}`}  >
             
              <div className='row inside-item my-3 mx-1 bg-light'>
              <div className="col-12 col-md-12 col-lg-4 ">
                  <h5 >{Title}</h5>
                   <img  src={Poster} alt="" />
               </div>
             
              </div>

            
            </NavLink>
           })}
           </div>
           </div>
           </div>
           </div>
           </div>




           <div className="menu-items container-fluid mt-5">
            <div className="row">
              <div className="col-11 mx-auto">
                 <div className="row my-5">
                    {movie.map((current,index)=>{
                const{imdbID,Title,Poster}=current;
              return <NavLink to={`/movies/${imdbID}`}  >
            
                            <div className="item1 col-12 col-lg-6 col-md-6 col-xl-4" >
                               <div className="row Inside-item my-3 mx-1 bg-light" >
                                {/**for images */} 
                                <div className="col-12 col-md-12 col-lg-4 ">
                                    <img src={img} alt="" />
                                </div>

                              </div>
                          </div>
                           
                       </NavLink>
                      })}
                  </div>
               </div>
             </div>
        </div> 
           
    </>
    );
}

export default Movie;
